choose.when:
    			SELECT B.* FROM (
				SELECT ROW_NUMBER() OVER() as r, A.* FROM (
				SELECT * FROM admin_board WHERE title LIKE CONCAT('%', #{search}, '%') ORDER BY no DESC) A 
				) B WHERE B.r BETWEEN #{begin} AND #{end}

choose.otherwise:
    			SELECT B.* FROM (
				SELECT ROW_NUMBER() OVER() as r, A.* FROM (
				SELECT * FROM admin_board ORDER BY no DESC) A 
				) B WHERE B.r BETWEEN #{begin} AND #{end}
